Bottom-up DP is always faster but for those who prefer top-down DP:

	class Solution {
	public:
		 int v[4][1000000];                                     //v[level][calculated value till this level]
		int recur(vector<int>&n,int lev,int cur){ 
			// base cases
			 if(lev>3){return 0;}
			 if(lev==3 && cur==0){return 1;}                    //if AND triplet value is 0 at level 3
			if(v[lev][cur]!=-1){return v[lev][cur];}  
			if(lev<3 && cur==0){return v[lev][cur]=pow(n.size(),3-lev);} //if at any point AND triplet value equals 0,
																		 //all substrees return constant value

			//explore possible routes
			int i,sum=0;       
			for(i=0;i<n.size();i++){
				if(lev==0){sum+=recur(n,lev+1,n[i]);}
				else {sum+=recur(n,lev+1,cur&n[i]);}
			}        
			return v[lev][cur]=sum; //memoize        
		}  

		int countTriplets(vector<int>& n) { 		
			memset(v,-1,sizeof(v));      
			return recur(n,0,1);        
		}
	};

