class Solution {
public:
    int countTriplets(vector<int>& A) {
        int MAX=(1<<16)-1;
        int dp[MAX+1];
        memset(dp,0,sizeof(dp));
        for(auto i:A){
            dp[i]++;
        }
        for(int i=0;i<16;i++){
            for(int j=0;j<=MAX;j++){
                if((1<<i)&j){
                    dp[j]+=dp[j^(1<<i)];
                }
            }
        }
        int tot=0;
        int n=(int)A.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                int num=A[i]&A[j];
                num=num^MAX;
                tot+=dp[num];
            }
        }
        return tot;
    }
};
