class Solution {
public:
    int countTriplets(vector<int>& nums) {
        int table[1<<16] = {0};
        int n = nums.size();
        for (int i = 0; i < n; ++i) {
            ++table[nums[i]];
            for (int j = i+1; j < n; ++j) {
                table[nums[i]&nums[j]]+= 2;
            }
        }
        int res = 0;
        for (int i: nums) {
            i = i ^ 0xffff;
            for (int j = i; j; j = (j-1)&i) {
                res += table[j];
            }
            res += table[0];
        }
        return res;
    }
};
