class Solution:
    def countTriplets(self, A: 'List[int]') -> 'int':

        tmp = []
        maxlen = 0
        for a in A:
            tmp.append(bin(a)[2:])
            maxlen = max(maxlen, len(tmp[-1]))
        pool = []
        for s in tmp:
            extra = maxlen - len(s)
            pool.append('0'*extra + s)

        row, col = len(pool), len(pool[0])
        one = collections.defaultdict(set)
        for j in range(col):
            for i in range(row):
                if pool[i][j] == '1':
                    one[j].add(i)

        Venn = collections.defaultdict(list)
        cnt = 0
        for j in range(col):
            if len(one[j]) != 0:
                cnt += (len(one[j]))**3
                for i in range(j, 0, -1):
                    for prv in Venn[i]:
                        intersec = prv & one[j]
                        if len(intersec) != 0:
                            cnt += ((-1)**i)*(len(intersec))**3
                            Venn[i+1].append(intersec)
                Venn[1].append(one[j])

        return row**3 - cnt
