class Solution {
    public int countTriplets(int[] nums) {
        int max = 0;
        for (int num : nums) {
            max = Math.max(max, num);
        }
        int N = 1;
        while (N <= max) {
            N <<= 1;
        }
        int[] cnt = new int[N];
        for (int x : nums) {
            for (int y : nums) {
                cnt[x & y]++;
            }
        }
        int ans = 0;
        for (int num : nums) {
            int subset = num ^ (N - 1);
            ans += cnt[0];
            for (int i = subset; i > 0; i = subset & (i - 1)) {
                ans += cnt[i];
            }
        }
        return ans;
    }
}
