    public int dfs(int A[],int cur,int level){
        if(level==2){
            if(cur!=0)return 0;
            return 1;
        }
        if(dp[cur][level]!=-1)return dp[cur][level];
        int res=0;
        for(int n:A){
            res+=dfs(A,cur&n,level+1);
        }

        dp[cur][level]=res;
        return res;
    }
}
