int countTriplets(int* A, int ASize){
    int cnt[1 << 16] = { 0 };
    for (int i = 0; i < ASize; i++) {
        cnt[A[i]]++;
        for (int j = i + 1; j < ASize; j++) {
            cnt[A[i] & A[j]] += 2;
        }
