    vector<int>& getIntersect(int mask, vector<vector<int>>& indices) {
        if (table[mask] >= 0)
            return intersect[mask];

        if (!mask) {
            table[mask] = 0;
            return intersect[mask];
        }
        vector<int> nonZeroBits;
        for (int b = 0; b < 16; ++b) {
            if (mask & (1 << b)) {
                nonZeroBits.push_back(b);
            }
        }

        if (nonZeroBits.size() == 1) {
            intersect[mask] = indices[nonZeroBits[0]];
            table[mask] = intersect[mask].size();
            return intersect[mask];
        }

        int m1 = 0, m2 = 0;
        int total = nonZeroBits.size();
        for (int i = 0; i < total; ++i) {
            int b = nonZeroBits[i];
            if (i <= (total - 1)/2) {
                m1 = (m1 | (1 << b));
            } else {
                m2 = (m2 | (1 << b));
            }
        }

        // Recursive call for masks m1 and m2
        vector<int>& v1 = getIntersect(m1, indices);
        vector<int>& v2 = getIntersect(m2, indices);

        // Two pointer approach to get the intersection
        int p1 = 0, p2 = 0;
        while (p1 < table[m1] && p2 < table[m2]) {
            if (v1[p1] < v2[p2]) {
                ++p1;
            } else if (v1[p1] > v2[p2]) {
                ++p2;
            } else {
                intersect[mask].push_back(v1[p1]);
                ++p1;
                ++p2;
            }
        }

        table[mask] = intersect[mask].size();
        return intersect[mask];
    }
private:
    vector<vector<int>> intersect;
    vector<int> table;
};
```

