class Solution {
public:
    int dp[66000][5];
    int fun(int val,int size,vector<int>&nums){
        if(size>3)return 0;
        if(val==0&&size==3)return 1;
        if(dp[val][size]!=-1)return dp[val][size];
        int ans=0;
        for(auto it:nums){
            if(size==0) ans+=  fun(it,size+1,nums);

            else  ans+= dp[val][size]=  fun(val&it,size+1,nums);

        }
        return dp[val][size]=ans;
    }
    int countTriplets(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return fun(0,0,nums);
    }
};
