class Solution {
    int count[65536];
public:
    int countTriplets(vector<int>& nums) {
        int ans = 0,n = nums.size();
        vector<int> prods;
        memset(count,0,sizeof(count));
        for(int i=n-1;i>=0;i--){
            ans += (nums[i]==0);
            for(auto p:prods){
                if((nums[i]&p) == 0)
                    ans += 6*count[p];
            }
            for(int j=i+1;j<n;j++){
                if((nums[j]&nums[i]) == 0)
                    ans += 6;
                if(!(count[nums[j] & nums[i]]))
                    prods.push_back(nums[j] & nums[i]);
                count[nums[j] & nums[i]]++;
            }

        }
        return ans;
    }
};
```

