class Solution {
public:
    void traverse(int i, unsigned curNum, unsigned num, int cnt[], int& res) {
        if (i == 16) {
            res += cnt[curNum];
            return;
        }

        if (((num >> i) & 1) == 0) {
            traverse(i+1, curNum, num, cnt, res);
            traverse(i+1, curNum + (1 << i), num, cnt, res);
        } else {
            traverse(i+1, curNum, num, cnt, res);
        }
    }

    int countTriplets(vector<int>& nums) {
        int res = 0;
        int cnt[1 << 16] = {0};

        for (int i = 0; i < nums.size(); ++i) {
            for (int j = 0; j < nums.size(); ++j) {
                cnt[nums[i] & nums[j]]++;
            }
        }

        for (auto num: nums) {            
            traverse(0, 0, num, cnt, res);
        }

        return res;
    }
};
```

