class Solution:
    def countTriplets(self, nums: List[int]) -> int:
        d = {}
        for i in nums:
            for j in nums:
                if (i & j) not in d:
                    d[i & j] = 0
                d[i & j] = d[i & j] + 1
        c = 0
        for i in d:
            for j in nums:
                if i & j == 0:
                    c = c + d[i]
        return c
