#define REP(i, n) for(int i = 0; i < n; i++)
#define REPP(i, n) for(int i = 1; i <= n; i++)
#define ALL(obj) (obj).begin(), (obj).end()

class Solution {
public:
    int countTriplets(vector<int>& A) {
        int n = A.size();
        int cnt[1<<16] = {};
        REP(i, n) REP(j, n) cnt[A[i] & A[j]]++;
        int ans = 0;
        REP(k, n){
            int cA = (1<<16) - 1 - A[k];
            for(int now = cA; now >= 1; now = (now - 1) & cA) ans += cnt[now];
            ans += cnt[0];
        }
        return ans;    
    }
};
```

