import kotlin.collections.HashMap

class Solution {
    fun countTriplets(A: IntArray): Int {
             val map = HashMap<Int, Int>()
        var ret = 0
        val n = A.size
         for (i in A.indices) {
            for (j in A.indices) {
                val twoAnd = A[i] and A[j]
                if (map.containsKey(twoAnd)) {
                    map[twoAnd] = (map[twoAnd] ?: 0) + 1
                } else {
                    map[twoAnd] = 1
                }
            }

        }

       A.forEach { a ->
            map.keys.forEach { b ->
                if ((a and b) == 0) {
                    map[b]?.let {
                        ret += it
                    }
                }

            }
        }
        return ret
    }
}
