public int CountTriplets(int[] nums) 
{
	Dictionary<int, int> counts = new Dictionary<int, int>();

	for(int i = 0; i < nums.Length; i++)
	{
		for(int j = 0; j < nums.Length; j++)
		{
			int andResult = nums[i] & nums[j];
			if(counts.ContainsKey(andResult))
