// attempt 2, to leverage the fact that a & b is in the range of two bytes
// (result num is 65536 at most)
// so we have to compute 2^16 * 1000 at most

func countTriplets(A []int) (result int) {
	n := len(A)

	//A[i]&A[j] -> array of j+1
	from := make(map[int][]int)

	for i := range A {
		if A[i] == 0 {
			result += 1 //1-dups
		}
		for j := i + 1; j < n; j++ {
			c := A[i] & A[j]
			from[c] = append(from[c], j+1)
			if c == 0 {
				result += 6 //2-dups
			}
		}
	}

	//3-dups
	for c, pos := range from {
		for _, start := range pos {
			for k := start; k < n; k++ {
				if c&A[k] == 0 {
					result += 6
				}
			}
		}
	}
	return
}


// A more concise one (with regard to code) that does not
// need to wield dicuss on situation, and seems to be faster on the test cases

func countTriplets(A []int) (result int) {
	count := make(map[int]int)
	for i := range A {
		for j := range A {
			x := A[i] & A[j]
			count[x] = count[x] + 1
		}
	}
	for x, c := range count {
		for i := range A {
			if x&A[i] == 0 {
				result += c
			}
		}
	}
	return
}
