class Solution {
    int dp[][];
    int nums[];
    boolean visit[][];
    public int countTriplets(int[] nums) {
        this.nums=nums;
        int max=0;
        for(int n:nums)max=Math.max(n,max);
        dp=new int[max+1][3];
        visit=new boolean[max+1][3];
        int res=0;
        for(int n:nums){
            res+=dfs(n,0);
        }
        return res;
    }
    public int dfs(int num,int level){
        if(level==2){
            if(num==0)return 1;
            return 0;
        }
        int ans=0;
        for(int n:nums){
            if(!visit[num&n][level+1]){
                dp[num&n][level+1]=dfs(num&n,level+1);
                visit[num&n][level+1]=true;
            }
            ans+=dp[num&n][level+1];
        }
        return ans;
    }
}
