```Scala
  def countTriplets(array: Array[Int]): Int = {
    val len = array.length
    if (len == 1) return if (array(0) == 0) 1 else 0
    var initialState = 0
    for (i <- 0 until 16) initialState |= (1 << i)

    import scala.collection.mutable.Map
    val cc = Map[String, Int]()

    def f(index: Int, count: Int, prevState: Int): Int = {
      if (index >= len) return 0
      if (count > 3) return 0
      if (count == 3) return if (prevState == 0) return 1 else 0
      val key = index + "&" + count + "&" + prevState
      if (cc.contains(key)) return cc(key)
      var res = 0
      for (i <- index until len) res += f(index, count + 1, prevState & array(i))
      cc.put(key, res)
      res
    }
    f(0, 0, initialState)
  }
