class Solution {
    public int countTriplets(int[] a) {

        if( a == null || a.length == 0 ) return 0;
        int n = a.length;
        int[] mem = new int[1<<16];
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                int key=a[i]&a[j];
                mem[key]++;
            }
        }
        int cnt=0;
        for(int i=0;i<(1<<16);i++){
            if(mem[i]==0) continue;
            for(int j=0;j<n;j++){
                if( (i&a[j]) == 0) {
                    cnt+=mem[i];
                }
            }
        }
        return cnt;
    }
}
