class Solution {
    public int countTriplets(int[] A) {
        int ans = 0;
        int N = A.length;
        for(int k = 1; k < (1 << 16); k++) {
            int count = 0;
            for(int i = 0; i < N; i++) {
                if((A[i] & k) == k) count++;
            }
            String s = Integer.toString(k, 2);
            int c = 0;
            for(int i = 0; i < s.length(); i++) {
                if(s.charAt(i) == '1') c++;
            }
            if(c % 2 == 1) ans += count * count * count;
            else ans -= count * count * count;
        }
        return N*N*N - ans;
    }
}
