func countTriplets(A []int) int {
    temp := make([]int, 1 << 16)
    for i := range A {
        temp[A[i]] = temp[A[i]] + 1
        for j := 1; j < len(A) - i;j = j + 1  {
            temp[A[i] & A[i:len(A)][j]] = temp[A[i] & A[i:len(A)][j]] + 2
        }
    }

    result := 0
    for i := range A {
        for j := range temp {
            if A[i] & j == 0 {
                result = result + temp[j]
            }
        }  
    }

    return result
}
