class Solution {
public:
    int countTriplets(vector<int>& A) {
        int N = A.size(), ret = 0;

        for (int i = 0; i < N; i++){
            if (A[i] == 0){
                ret++;
            }
            for (int j = i + 1; j < N; j++){
                if ((A[i] & A[i] & A[j]) == 0) {
                    ret += 3; // "i i j" can be ordered 3 different ways.
                }
				// Permutation for P(3,3)
                if ((A[j] & A[j] & A[i]) == 0) {
                    ret += 3; // "i j j" can be ordered 3 different ways.
                }
                for (int k = j + 1; k < N; k++){
                    if ((A[i] & A[j] & A[k]) == 0){
                        ret += 6; // "i j k" can be ordered 6 different ways.
                    }
                }
            }
        }

        return ret;
    }
};
