var countTriplets = function(A) {
    var count = 0;
    var countMap = {};

    _.each(A, i => {
        _.each(A, j => {
            var t = i & j;
            if (!_.has(countMap, t)) {
                countMap[t] = 0;
                _.each(A, k => {
                    if ((k & t) === 0) countMap[t]++;
                })
            }
            count += countMap[t];
        });
    });
    return count;
};
