//  count[i] :  # A[j]=i
//  f[i]         :  # A[j] that the positions where i (regarded as 16-bit string) is 0, is a subset of those of A[j] (eg, i=101011  A[j] should be x0x0xx)

```

class Solution {
public:
    int countTriplets(vector<int>& A) {
        int count[(1<<16)+5],f[(1<<16)+5],S=(1<<16),n=A.size();
        for(int i=0;i<S;i++)f[i]=count[i]=0;
        for (int i=0;i<n;i++)count[A[i]]++;
        for (int i=0;i<S;i++)
        {
            if(count[i]==0)continue;

            //if(i!=3)continue;

            int m=0;
            vector<int> t=vector<int>(17,0);
            for (int j=0;j<16;j++)if(((1<<j)&i)==0)
            {
                m++;
                t[m]=j;
            }
            if(m==0)
            {
                f[i]+=count[i];
                continue;
            }
            for(int k=0;k<(1<<m);k++)
            {
                int tmp=i;
                for(int j=0;j<m;j++)if((k&(1<<j))!=0)
                {
                    tmp=tmp|(1<<t[j+1]);
                }
                f[tmp]+=count[i];
            }
        }

        int res=0;
        for(int i=0;i<n;i++)
            for(int j=0;j<n;j++)
            {

                res+=f[(S-1)^(A[i]&A[j])];
            }
        return res;
    }
};
```

