func simplifyPath(_ path: String) -> String {
    "/" + path.split(separator: "/").reduce(into: [Substring]()) {
        switch $1 {
        case ".": return
        case "..": _ = $0.popLast()
        default: $0.append($1)
        }
    }.joined(separator: "/")
}
