char * simplifyPath(char * path){
    int len = strlen(path) + 1;
    char** pathArray = (char**)calloc(len,sizeof(char*)); ;
    int idx = 0;
    char* p;
    p = strtok(path, "/");
    while (p != NULL) {
        if(strcmp(p, "..") == 0){
            if(idx-1 >= 0) idx -= 1;
        }
        else if(strcmp(p, ".") != 0){
            pathArray[idx] = (char*)calloc(strlen(p) + 1,sizeof(char)); 
            strcpy(pathArray[idx], p);
            idx += 1;
        }
        p = strtok(NULL, "/");		   
    }
    char* result = (char*)calloc(len,sizeof(char)); 
    for(int i=0; i<idx; i++){
        strcat(result,"/");
        strcat(result,pathArray[i]);
    }
    if(idx==0) strcat(result,"/");
    return result;
}
