class Solution {
    func simplifyPath(_ path: String) -> String {
        var stack: [Substring] = []

        for p in path.split(separator: "/") {
            if p == ".." { stack.popLast() }
            else
                if p != "." { stack.append(p) }
        }
        return "/" + stack.joined(separator: "/")
    }
}
