/**
 * @param {string} path
 * @return {string}
 */
var simplifyPath = function(path) {
    const res = [];
    const arr = path.split("/");
    for(let str of arr) {
        // when you see .. symbol
        // check result array and if it's not empty
        // pop the last element
        if(str === "..") {
            if(res.length !== 0) {
                res.pop();
            }
        } 
        // if you split "a//b" the result ['a','','b']
        // So check the length>=1 and ignore the period
        else if(str.length>=1 && str!==".") {
            res.push(str);
        } 
    }
    return "/"+res.join("/");
};
