                    }
                    if(j==-1) j=0;
                }
            }
            else{
                    stack[++j]=path.charAt(i);
                    i++;
                } 
        }
        if(stack[j]=='/'&& j>1) j--;

        return String.valueOf(stack,0,j+1);
  }
}
