class Solution{
  public String simplifyPath(String path) {
       Stack<String> s=new Stack<>();
       StringBuilder res=new StringBuilder();
       String[] p=path.split("/");

       for(int i=0;i<p.length;i++){
         if(!s.isEmpty() && p[i].equals("..")) s.pop();
         if(!p[i].equals("") && !p[i].equals(".") && !p[i].equals("..")){
