class Solution {
public:
    string simplifyPath(string path) {
       int size=path.size(),i=0;
        if(size==1)
            return path;
        stack<string> sta;
        while(i<size){
            if(path[i]=='/'){
                i++;
                continue;
            }
            string tmp="";
            while(i<size && path[i]!='/')
                tmp+=path[i++];
            if(tmp == "." || tmp == "")
                continue;
            else if(tmp == ".."){
                if(sta.size())
                    sta.pop();
            }
            else
                sta.push(tmp);
        } 
        if(sta.size()==0)
            return "/";
        string ans="";
        while(sta.size()){
            ans="/"+sta.top()+ans;
            sta.pop();
        }
        return ans;
    }
};
