class Solution {
public:
    int titleToNumber(string columnTitle) {
        int result=0;

        for(int i=0;i<columnTitle.size();i++)
        {
            result*=26;
            // columnTitle[i] --> YE CURRENT ELEMENT KA ASCII CODE DEGA
            result+=columnTitle[i]-'A'+1;
        }
        return result;

    }

    // EX--> AB
    // RES=26*0,   RES=0
    // RES= 0 + (65-65)+1
    //   65 IS ASCII CODE OF A 
    /// SO FINALLY 1 IS STORED IN RESULT

    /// NOW INCREMENT I==B 

    //RES=1*26 RES=52
    // RES=26+(66-65)+1
    // RES=28   AND THAT IS THE REQUIRED ANSWER
};
```

