class Solution {
private:
    long solve(string &s,int index){
        if(index == 0) return s[index] - 'A' + 1;

        long rest = solve(s,index - 1);
        return 26 * rest + s[index] - 'A' + 1;
    }
public:
    int titleToNumber(string columnTitle) {
        int n = columnTitle.size() - 1;
        return solve(columnTitle,n);
    }
};
```
