class Solution(object):
    def getSum(self, a, b):
        """
        :type a: int
        :type b: int
        :rtype: int
        """

        max_int = 0x7FFFFFFF
        mask = 0xFFFFFFFF
        # do the loop until there is no overflow in addition
        while b != 0:
            a, b = (a ^ b) & mask, ((a & b) << 1) & mask
        # when a > max_int, that means a is an
        if a <= max_int:
            return a
        else:
            return ~(a ^ mask)
