```
    int flipLights(int n, int m) {

        // The bulb operations will always end up the same for each group of 4 bulbs
        // Therefore, we have a max of 2^4 possible states or less for smaller n

        int states;
        if (n > 3)
            states = pow(2, 4);
        else
            states = pow(2, n);

        // There are 4 possible flips, 15 = 1111, 10 = 1010, 5 = 0101, 1 = 0001
        int flips[4] = {15, 10, 5, 1};

        // Initially we have only one state possible out of 16 (for n > 3): 1000000000000000
        int lastStates = (1 << (states - 1)), newStates = 0;

        // Iterate through all bulb operations (I think this can be reduced so it doesn't have to go to m)
        for (int i = 0; i != m; ++i, lastStates = newStates, newStates = 0)
            for (int j = 0; j != states; ++j)
                if ((lastStates & (1 << j)) == (1 << j))            // if state j was a possibility in the last iteration
                    for (int k = 0; k != 4; ++k)                    // perform each bulb operation and flag that the state is now possible
                        newStates  |= (1 << ((states - 1) & (j ^ flips[k])));

        // count up our bits/states
        int count = 0;
        for (int i = 0; i != states; count += (lastStates & (1 << i)) == (1 << i), ++i);

        return count;
    }

