# Code
```java []
class Solution {

    public int flipLights(int n, int p) {
        n = Math.min(n, 4); 
        p = Math.min(p, 4);
        int thre = (1<<n)-1;

        int[] flips = new int[] {
            Integer.parseInt("1111", 2)&thre,
            Integer.parseInt("0101", 2)&thre,
            Integer.parseInt("1010", 2)&thre,
            Integer.parseInt("1001", 2)&thre
        };

        if(p==0) return 1;

        int ans = 0;
        boolean[] used = new boolean[(1<<10)+1];

        Queue<int[]> q = new LinkedList();
        q.add(new int[] {0, 0});

        while(!q.isEmpty()){
            int[] val = q.remove();
            int cur = val[0];
            int presses = val[1];

            if(presses==p) {
                if(!used[cur]) ans++;
                used[cur] = true;
                continue;
            }

            for(int flip : flips){
                q.add(new int[] {flip ^ cur, presses + 1});
            }          
        }

        return ans;
    }
}
```

```python3 []
use std::collections::{HashSet, VecDeque};

impl Solution {
    fn flip_bit(number: i32, position: u32) -> i32 {
        let mask = 1 << position;
        let flipped_number = number ^ mask;
        flipped_number
    }

    fn y_function(x: i32) -> i32 {
        3 * x + 1
    }

    fn get_neighbors(state: i32, n: i32) -> Vec<i32> {
        let mut nbrs: Vec<i32> = vec![];

        let mut curr_num = state;
        for i in 1..n {
            curr_num = Solution::flip_bit(curr_num, i as u32);
        }
        nbrs.push(curr_num);

        curr_num = state;
        for i in (1..n).step_by(2) {
            curr_num = Solution::flip_bit(curr_num, i as u32);
        }
        nbrs.push(curr_num);

        curr_num = state;
        for i in (0..n).step_by(2) {
            curr_num = Solution::flip_bit(curr_num, i as u32);
        }
        nbrs.push(curr_num);

        let mut i = 0;
        while Solution::y_function(i) < n {
            curr_num = Solution::flip_bit(curr_num, Solution::y_function(i) as u32);
            i += 1;
        }
        nbrs.push(curr_num);

        nbrs
    }

    // O(n*press) time,
    // O(n*space) space,
    // Approach: bfs, bit manipulation
    fn flip_lights(n: i32, presses: i32) -> i32 {
        let mut answer: HashSet<i32> = HashSet::new();
        let mut queue: VecDeque<i32> = VecDeque::new();
        queue.push_back((2_i32.pow(n as u32) - 1));
        answer.insert(queue[0]);

        let mut presses = presses;

        while presses > 0 && !queue.is_empty() {
            presses -= 1;
            let queue_len = queue.len();
            answer = HashSet::new();
            for _ in 0..queue_len {
                let state = queue.pop_front().unwrap();
                let nbrs = Solution::get_neighbors(state, n);
                for nbr in nbrs {
                    if answer.contains(&nbr) {
                        continue;
                    }
                    answer.insert(nbr);
                    queue.push_back(nbr);
                }
            }
        }

        answer.len() as i32
    }
}
```
```c++ []
class Solution {
public:
    int flipLights(int n, int presses) {
        vector<bitset<1000>> m(4);

        for (int i=1;i<n;i+=2) {
            m[1].flip(i);
            m[2].flip(i-1);
        }
        if (n%2==1) m[2].flip(n-1);
        m[0]=m[1];
        m[0]|=m[2];


        for (int i=0;i<n;i+=3) m[3].flip(i);

        //for(auto& a:m) cout << a << endl;

        unordered_set<bitset<1000>> q;
        q.insert(m[0]);

        for (int i=0;i<presses;i++){
            unordered_set<bitset<1000>> temp;
            for (auto& item:q){
                for (int j=0;j<4;j++){
                    auto t = bitset<1000>{item};
                    t ^= m[j];
                    temp.insert(t);
                }
            }
            q = temp;
        }
        return q.size();

    }
};
```

