object Solution {
  def flipLights(n: Int, presses: Int): Int = {
    type Status = Set[Int] // contains indexes of bulbes on

    val allOn: Status = Set.from(1 to n)

    def flip(s: Status)(c: Int => Boolean): Status = allOn.filter {
      case b if s(b) => !c(b)
      case b => c(b)
    }

    def button1(s: Status): Status = flip(s)(_ => true)
    def button2(s: Status): Status = flip(s)(_ % 2 == 0)
    def button3(s: Status): Status = flip(s)(_ % 2 == 1)
    def button4(s: Status): Status = flip(s)(_ % 3 == 1)

    val mem = scala.collection.mutable.Map[Status, Set[Status]]()

    def statusesFrom(s: Status): Set[Status] = {
      mem.getOrElseUpdate(s,
        Set(button1(s), button2(s), button3(s), button4(s))
      )
    }

    val statuses = LazyList.iterate(Set(allOn)) {
      _.flatMap(statusesFrom)
    }

    statuses(presses).size
  }
}
