        string op(int type,string& s){
        if(type==0){
            for(int i = 0;i<s.length();i++){
                if(s[i]=='1') s[i]='0';
                else s[i]='1';
            }
        }
        else if(type==1){
            for(int i = 1;i<s.length();i+=2){
                if(s[i]=='1') s[i]='0';
                else s[i]='1';
            }
        }
        else if(type==2){
            for(int i = 0;i<s.length();i+=2){
                if(s[i]=='1') s[i]='0';
                else s[i]='1';
            }
        }
        else{
            for(int k = 0;3*k+1<=s.length();k++){
                int i = 3*k;
                if(s[i]=='1') s[i]='0';
                else s[i]='1';
            }
        }
        return s;
    }
    void solve(string s,int presses,unordered_map<string,unordered_map<int,int>>&dp){
        if(presses == 0){
            st.insert(s);
            return;
        }

        if(dp.count(s) and dp[s].count(presses)) return;

        dp[s][presses] = 1;

        solve(op(0,s),presses-1,dp); 
        solve(op(1,s),presses-1,dp); 
        solve(op(2,s),presses-1,dp); 
        solve(op(3,s),presses-1,dp); 
    }
    int flipLights(int n, int presses) {
        string s(n,'1');
        unordered_map<string,unordered_map<int,int>>dp;
        solve(s,presses,dp);
        return st.size();       
    }
};
