class Solution {

    // Let a = sum(max value of each row)
    // Let b = sum(max value of each colomn)
    // Let c = count(grid[i][j], where grid[i][j] > 0)
    // ans = c + a + b
    public int projectionArea(int[][] grid) {
        int M = grid.length;
        int N = grid[0].length;

        int[] leftProj = new int[M];
        int[] topProj = new int[N];

        int a = 0;
        int c = 0;
        for (int i = 0; i < M; i++) {
            for (int j = 0 ; j < N; j++) {
                leftProj[i] = Math.max(leftProj[i], grid[i][j]);
                if (grid[i][j] > 0) c++;
            }
            a += leftProj[i];
        }

        int b = 0;
        for (int j = 0; j < N; j++) {
            for (int i = 0; i < M; i++) {
                topProj[j] = Math.max(topProj[j], grid[i][j]);
            }
            b += topProj[j];
        }

        return c + a + b;
    }
}
