var projectionArea = function(grid) {
    var top = 0;
    var len = grid.length;
    var front = Array(len).fill(0);
    var side = Array(len).fill(0);
    for (var i = 0; i < len; i++) {
        for (var j = 0; j < len; j++) {
            front[i] = Math.max(front[i], grid[i][j]);
            side[j] = Math.max(side[j], grid[i][j]);
            if (grid[i][j] > 0) {
                top++;
            }
        }
    }

    var frontSum = front.reduce((sum, current) => sum + current);
    var sideSum = side.reduce((sum, current) => sum + current);

    return top + frontSum + sideSum;
};
