class Solution {
public:
    int projectionArea(vector<vector<int>>& grid) {
        int sum = 0, sz=grid.size(), cnt=0;
        // rowm = row Wise Max Values && 
        // colm = column Wise Max Values
        vector<int> rowm, colm;
        for(int i=0; i<sz; i++){
            for(int j=0; j<grid[i].size(); j++){

                if(grid[i][j] > 0) cnt++;

                if(rowm.size() <= j)
                   rowm.push_back(grid[i][j]);
                else
                    rowm[j] = max(rowm[j], grid[i][j]);

                if(colm.size() <= i)
                    colm.push_back(grid[i][j]);
                else
                    colm[i] = max(colm[i], grid[i][j]);
            }
        }

        for(int i : rowm)
            cnt += i;
        for(int i : colm)
            cnt += i;

        return cnt;
    }
};
```

