class Solution:
    def projectionArea(self, grid: List[List[int]]) -> int:

        ans = len(grid) * len(grid[0])

        ic = [0] * len(grid[0])

        for i in grid:
            ans += max(i)

            for m,n in enumerate(i):
                if n == 0:
                    ans -= 1

                if n > ic[m]:
                    ic[m] = n

        ans += sum(ic)

        return ans

