class Solution {
public:
    int projectionArea(vector<vector<int>>& grid) {
        int r=grid.size();
        int c=grid[0].size();
        int area=0;
        for(auto it:grid){
            for(auto i:it){
                if(i!=0){
                    area++;
                }
            }
        }

        int maxi=0;
        int a,b;
        int tempa;
        int max=0;
        int tempb;
        int maxy=0;
        vector<int>maximum;
        for(auto it:grid){
