            maximum.push_back(max);
            if(max>maxy){
                maxy=max;
            }
            max=0;
        }
        tempb=maxy*r;

        for(auto i:maximum){
            if(i!=maxy){
                tempb=tempb-(maxy-i);
            }
        }
        area+=tempb;

    return area;
    }
};
