var projectionArea = function(grid) {
    function topProjection(grid){
        var topCount = 0;
        for (i=0;i<grid.length;++i){
            for (j=0;j<grid[i].length;++j){
                if (grid[i][j] > 0){
                   topCount++; 
                }
            }
        }
        return topCount;
