      function yProjection(grid){
        //biggest in each index
        var maxArr = [];
        for (i=0;i<grid[0].length;++i){  
        size = grid.length;
            let newMax = 0;
            for (j=0;j<size;++j){
                  newMax = Math.max(grid[j][i], newMax);          
