        //biggest in each sub array
        var count = 0;
        for (i=0;i<grid.length;++i){
            var bigNum = 0;
            for (j=0;j<grid[i].length;++j){
                if (grid[i][j]>bigNum){
                   bigNum = grid[i][j];
                }
            }
            count = bigNum + count;
        }
        return  count;
    }
    console.log('top '+topProjection(grid) + 'x '+ xProjection(grid) + 'y '+ yProjection(grid));
   return topProjection(grid) + xProjection(grid) + yProjection(grid);
};
