class Solution {
public:
    vector<int> addToArrayForm(vector<int>& A, int K) {
        vector<int>v;
        string p=to_string(K);
        int l1=A.size()-1;
        int l2=p.size()-1;
        int carry=0;
        int i=0;
        while(l1>=0 || l2>=0){

            int sum=carry;
            //here sum=sum+carry would be same as this s have written it like this only
            if(l1>=0){
                sum=sum+A[l1--];
            }
            if(l2>=0){
                sum=sum+p[l2--]-'0';
            }
            v.push_back(sum%10);
            carry=sum/10;

        }
        if(carry>0){
            v.push_back(carry);
        }
        reverse(v.begin(),v.end());

        return v;
    }
};
