    vector<int> addToArrayForm(vector<int>& num, int k) {
        short carry = 0;

        for (int i = num.size() - 1; i >= 0; i--) {
            short sum = num[i] + (k % 10) + carry;

            if (sum > 9) {
                num[i] = sum % 10;
                carry = sum / 10;
                k /= 10;
            } else {
                num[i] = sum;
                k /= 10;
                carry = 0;
            }
