        while (k || carry) {
            short sum = (k % 10) + carry;

            if (sum > 9) {
                carry = sum / 10;
                num.insert(num.begin(), sum % 10);
                k /= 10;
            } else {
                num.insert(num.begin(), sum);
                carry = 0;
                k /= 10;      
            }
        }

        return num;
    }
};
